mapper.select:
select count(*) from member

	where member_code like #{keyword}


	where member_id like #{keyword}


	where member_name like #{keyword}


	where member_birth like #{keyword}


	where member_start like #{keyword}

select * from member

	where member_code like #{keyword}


	where member_id like #{keyword}


	where member_name like #{keyword}


	where member_birth like #{keyword}


	where member_start like #{keyword}

order by member_code desc
