mapper.select:
select * from movie

	where movie_title like #{keyword}

order by movie_num desc

mapper.update:
update movie
set vod = 1
where movie_code = #{movie_code}

update movie
set vod = 0
where movie_code = #{movie_code}

update vod
set vod_price = #{vod_price}, url = #{url}
where movie_code = #{movie_code}

mapper.select:
select * from Screen
where movie_title = #{movie_title}
order by time

select * from ticket_reservation
where screen_time = #{screen_time}
order by seat_number2

select * from ticket_reservation
where member_id = #{member_id}
order by seat_number2

mapper.update:
update screen
set left_seats = left_seats -1
where Screen_time = #{Screen_time}

mapper.select:
select movie_title,reservation_date,screen_name,listagg(seat_number,',') within group ( order by reservation_num)
from ticket_reservation where member_id = #{member_id}
group by movie_title,reservation_date,screen_name
